import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";

function Expenses(props) {
  const [yearEntered, setYearEntered] = useState("2020");
  const [expensesState, setExpensesState] = useState([props.expenses]);

  const YearFilterHandler = (year) => {
    setYearEntered(year);
    console.log(yearEntered);
    console.log(expensesState);

    setExpensesState(expensesState.filter(e => e.date.year === yearEntered));
  };

  // let expenses_render = [];
  // expenses_render = props.expenses.map((e, index) => (
  //   <ExpenseItem
  //     key={index}
  //     id={e.id}
  //     date={e.date}
  //     title={e.title}
  //     amount={e.amount}
  //   ></ExpenseItem>
  // ));

  return (
    <div className="expenses">
      <ExpensesFilter selected={yearEntered} onYearFilter={YearFilterHandler} />
      {/* {expenses_render} */}
      {props.expenses.map((e, index) =>
          <ExpenseItem
            key={index}
            id={e.id}
            date={e.date}
            title={e.title}
            amount={e.amount}
          ></ExpenseItem>
      )}
    </div>
  );
}

export default Expenses;
